mapper.select:
SELECT PROD_NAME,PROD_PRICE
FROM PROD INNER JOIN BUYER ON(BUYER_ID = PROD_BUYER)
WHERE PROD_BUYER = #{buyer_id}

SELECT A.* FROM ( SELECT ROWNUM RNUM , BUYER_ID,BUYER_NAME,BUYER_LGU, BUYER_BANK,BUYER_BANKNO,BUYER_BANKNAME, BUYER_ZIP,BUYER_ADD1,BUYER_ADD2, BUYER_COMTEL,BUYER_FAX,BUYER_MAIL,BUYER_CHARGER, LPROD_ID,LPROD_GU, LPROD_NM FROM BUYER INNER JOIN LPROD ON(BUYER_LGU = LPROD_GU)
) A
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}
